import Image from "next/image";

interface UploadImageProps {
  url: string;
  alt: string;
}

export function UploadImage({ url, alt }: UploadImageProps) {
  return (
    <Image
      src={url || "/placeholder.svg"}
      alt={alt}
      width={1920}
      height={1080}
      className="object-cover"
      // Add a blur placeholder while loading
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
      // Optional: Disable lazy loading if the image is above the fold
      // priority={true}
    />
  );
}
